option.value:
package vista;

import modelo.Proyecto;
import javax.swing.*;
import javax.swing.border.TitledBorder;
import java.awt.*;
import java.awt.datatransfer.*;
import java.awt.event.MouseAdapter;
import java.awt.event.MouseEvent;
import java.text.SimpleDateFormat;
import java.util.function.Consumer;

public class TarjetaProyecto extends JPanel {
    private Proyecto proyecto;
    private Consumer<String> onDropCallback;

    public TarjetaProyecto(Proyecto proyecto, boolean esArrastrable, Consumer<String> onDropCallback) {
        this.proyecto = proyecto;
        this.onDropCallback = onDropCallback;
        setLayout(new BorderLayout(5, 5));
        setBorder(BorderFactory.createLineBorder(Color.GRAY, 1));
        setBackground(Color.WHITE);

        if (esArrastrable) {
            setCursor(new Cursor(Cursor.MOVE_CURSOR));
            setTransferHandler(new TransferHandler() {
                @Override
                protected Transferable createTransferable(JComponent c) {
                    return new Transferable() {
                        private final DataFlavor flavor = new DataFlavor(TarjetaProyecto.class, "TarjetaProyecto");
                        @Override
                        public DataFlavor[] getTransferDataFlavors() {
                            return new DataFlavor[] { flavor };
                        }
                        @Override
                        public boolean isDataFlavorSupported(DataFlavor flavor) {
                            return this.flavor.equals(flavor);
                        }
                        @Override
                        public Object getTransferData(DataFlavor flavor) throws UnsupportedFlavorException {
                            if (isDataFlavorSupported(flavor)) {
                                return TarjetaProyecto.this;
                            }
                            throw new UnsupportedFlavorException(flavor);
                        }
                    };
                }
                @Override
                public int getSourceActions(JComponent c) {
                    return MOVE;
                }
            });
            addMouseListener(new MouseAdapter() {
                public void mousePressed(MouseEvent e) {
                    JComponent comp = (JComponent) e.getSource();
                    comp.getTransferHandler().exportAsDrag(comp, e, TransferHandler.MOVE);
                }
            });
        } else {
            setCursor(new Cursor(Cursor.HAND_CURSOR));
        }

        // --- UI de la tarjeta (sin cambios) ---
        JPanel panelHeader = new JPanel(new BorderLayout());
        panelHeader.setOpaque(false);
        JLabel lblNombre = new JLabel("<html><b>" + proyecto.getNombreProyecto() + "</b></html>");
        lblNombre.setFont(new Font("Segoe UI", Font.BOLD, 14));
        lblNombre.setBorder(BorderFactory.createEmptyBorder(5, 5, 0, 5));
        JButton botonDetalles = new JButton("...");
        botonDetalles.setPreferredSize(new Dimension(30, 20));
        botonDetalles.setMargin(new Insets(0,0,0,0));
        panelHeader.add(lblNombre, BorderLayout.CENTER);
        panelHeader.add(botonDetalles, BorderLayout.EAST);
        add(panelHeader, BorderLayout.NORTH);

        JPanel panelBody = new JPanel(new GridLayout(2, 1));
        panelBody.setOpaque(false);
        JLabel lblPrioridad = new JLabel("Prioridad: " + proyecto.getNombrePrioridad());
        lblPrioridad.setFont(new Font("Segoe UI", Font.PLAIN, 12));
        lblPrioridad.setBorder(BorderFactory.createEmptyBorder(0, 5, 0, 5));
        panelBody.add(lblPrioridad);
        JLabel lblPorcentaje = new JLabel("Avance: " + proyecto.getPorcentajeAvance() + "%");
        lblPorcentaje.setFont(new Font("Segoe UI", Font.PLAIN, 12));
        lblPorcentaje.setBorder(BorderFactory.createEmptyBorder(0, 5, 5, 5));
        panelBody.add(lblPorcentaje);
        add(panelBody, BorderLayout.CENTER);

        setPreferredSize(new Dimension(200, 100));
        setMaximumSize(new Dimension(Integer.MAX_VALUE, 100));
        setAlignmentX(Component.LEFT_ALIGNMENT);

        botonDetalles.addActionListener(e -> mostrarDetalles());
    }

    private void mostrarDetalles() {
        SimpleDateFormat sdf = new SimpleDateFormat("dd/MM/yyyy");
        String fechaInicio = (proyecto.getFechaInicio() != null) ? sdf.format(proyecto.getFechaInicio()) : "N/A";
        String fechaEstimada = (proyecto.getFechaFinalEstimada() != null) ? sdf.format(proyecto.getFechaFinalEstimada()) : "N/A";
        String mensaje = "<html><b>Nombre:</b> " + proyecto.getNombreProyecto() + "<br>" + "<b>Estado:</b> " + proyecto.getNombreEstado() + "<br>" + "<b>Prioridad:</b> " + proyecto.getNombrePrioridad() + "<br>" + "<b>Avance:</b> " + proyecto.getPorcentajeAvance() + "%<br>" + "<b>Fecha Inicio:</b> " + fechaInicio + "<br>" + "<b>Fecha Fin Estimada:</b> " + fechaEstimada + "<br>" + "<b>Descripción:</b> " + proyecto.getDescripcionProyecto() + "</html>";
        JOptionPane.showMessageDialog(this, mensaje, "Detalles del Proyecto: " + proyecto.getNombreProyecto(), JOptionPane.INFORMATION_MESSAGE);
    }

    public Proyecto getProyecto() { return proyecto; }
}

